sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	if(newValue){
		if(newValue != oldValue){
			var checkout_t = g_form.getValue('checkout_time');

			if(checkout_t != ''){
				var ajax = new GlideAjax('CheckoutFormUtils');
				ajax.addParam('sysparm_name','getNowDateTime');
				ajax.getXMLAnswer(timeResponse);
			}
		}
	}
}
function timeResponse(response){
	var checkout_t = g_form.getValue('checkout_time');
	var return_t = g_form.getValue('return_time');

	alert(response);

	if(checkout_t.getNumericValue() < response.getNumericValue()){
		g_form.setValue('checkout_time', '');
		g_form.showErrorBox('checkout_time', "Checkout Time cannot be in the past.");
	}

	if(checkout_t.getNumericValue() > return_t.getNumericValue()){
		g_form.setValue('checkout_time', '');
		g_form.showErrorBox('checkout_time', "Checkout Time cannot be after Return Time.");
	}
}
