sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}

	//Type appropriate comment here, and begin script below
	var chkTime = g_form.getValue('checkout_time');
	var ajax = new GlideAjax('CheckoutTimeUtils');
	ajax.addParam('sysparm_name','getReturnTime');
	ajax.addParam('sysparm_chkTime', chkTime);
	ajax.addParam('sysparm_device', newValue);
	ajax.getXMLAnswer(getTime);
}
function getTime(response){
	//var answer = response.responseXML.documentElement.getAttribute("answer");
	g_form.setValue('return_time', response);
}
