var CheckoutUtils = Class.create();
CheckoutUtils.prototype = {
	initialize: function() {
		/********************************
		Nothing for this area yet
 		********************************/
	},

	/*****************
	Sample function
 	*****************/
	sample: function(){
		//do something
	}, //do not forget the ,

	checkAvailabilityofDevice: function(name){
		var gr = new GlideRecord('x_casus_laptop_devices_table');
		gr.addQuery('device_id',name);

		//gr.addQuery("availability",1);
		gr.query();
		gr.next();
		//		gs.addInfoMessage(gr.availability!="Available");
		//		gs.addInfoMessage(gr.getValue("availablity")); // .getAttribute('availability'));
		if (gr.availability=="Available"||gr.availability==1){
			//gs.addInfoMessage("device is not available so Im sending false");
			return true;
		}
		return false;
	},

	checkForHolds: function(id){
		var gr = new GlideRecord('x_casus_laptop_hold');
		gr.addQuery('name',id);
		gr.addQuery('active',true);
		gr.query();
		if (gr.getRowCount()==0){
			return true;
		}else{
			return false;
		}
	},

	type: 'CheckoutUtils'
};
