sys_ui_action.script:
var isManager = gs.hasRole('x_casus_laptop.admin') || 
	gs.hasRole('x_casus_laptop.airc_manager') || 
	gs.hasRole('x_casus_laptop.asi_manager') || 
	gs.hasRole('x_casus_laptop.library_manager');

var isUnderRenewalLimit = current.renewal_count < current.device_name.device_type.max_renewal;

if (isManager || isUnderRenewalLimit){
	var now = new GlideDateTime();
	var return_time = new GlideDateTime(current.return_time);

	var isBeforeReturnTime = now.compareTo(return_time) == -1;

	if (isManager || isBeforeReturnTime){
		var i = current.renewal_count;
		i += 1;
		current.renewal_count = i;

		var newReturn = new GlideDateTime(); //automatically set to current Date/Time
		var default_time = new GlideDateTime(current.device_name.device_type.default_time);

		newReturn.add(default_time.getNumericValue());
		current.return_time = newReturn;
		//current.state="Confirming";
		current.update();

		gs.addInfoMessage(current.device_name.device_type.device_type + " " + current.device_name.device_id + 
						  " Renewed Until " + current.return_time.getDisplayValue());
	}else{
		gs.addErrorMessage('Renewal is not allowed past return time.');
		action.setRedirectURL(current);
	}
}else{
	gs.addErrorMessage("Can not check out device again!");
	action.setRedirectURL(current);
}
